int main()
{
    int flag=0,i,x,a[7],b[7];
    for(i=0;i<7;i++)
     scanf("%d %d",&a[i],&b[i]);
    for(i=0;i<7;i++)
    {
       if(a[i]+b[i]>8)
       {
          flag=1;
          x=i+1;
          break;
	   }
    }
    if(flag)
      printf("%d",x);
    else 
      printf("0"); 
	return 0;
}

#include<iostream>
using namespace std;
int main()
{
start:	int day=1;
	int a,b;
	int unhpy=0;
	int ex=0;
	while (cin>>a>>b)
	{
		int tot=a+b;
		if (tot>8 && tot-8>unhpy) 
			{
				unhpy=tot-8;ex=day;
			}
		if (++day==8) 
		{
			if (unhpy==0) cout<<0<<endl;
			else cout<<ex<<endl;
		}
	} 
	return 0;
}

