#include <stdio.h>
#include <stdlib.h>
int judge(int i)
{  int t0=0,t1=0;
   while(i!=0)
   {       
      if(i%2)  t1++;
      else  t0++;
      i/=2;     
             }
    if(t1>t0)  return 1;
    else return 0; 

    }
int main(int argc, char *argv[])
{
    int i,ta=0,tb=0;
  for(i=1;i<1001;i++)
  {
    if(judge(i)) ta++;
    else tb++;
                     }
    printf("%d %d\n",ta,tb);
  system("PAUSE");	
  return 0;
}

#include<stdio.h>
int main()
{
	int i,c,d=0,e=0,a,b;
	for(i=1;i<1000;i++)
	{
		c=i;a=0;b=0;
		while(c>0)
		{
			if(c%2==0)
			{
				a++;
				c=c/2;
			}
			else
			{
				b++;
				c=(c-1)/2;
			}
		}
		if(a>b)
			d++;
		else if(a<b)
			e++;
	}
	printf("%d %d",d,e);
	return 0;
}

var m,n,i,j,x,k,a,b:longint;
    c:array[0..1000]of longint;
begin
 for i:=1 to 1000 do
  begin
   j:=0;x:=i;a:=0;b:=0;
   while x>=1 do
    begin
     inc(j);
     c[j]:=x mod 2;
     x:=x div 2;
    end;
   for k:=1 to j do if c[k]=1 then inc(a)else inc(b);
   if a>b then inc(n)else inc(m);
  end;
 writeln(n);
 writeln(m);
end.

