#include<iostream>
using namespace std;

int r[] = {0, 0, 1, -1};
int c[] = {1, -1, 0, 0};

int maxi(int a, int b) {
	if(a > b) return a;
	return b;
}

int dfs(char graph[][100], int row, int col, int maxRow, int maxCol) {
	if(row < 0 || row >= maxRow) return 0;
	if(col < 0 || col >= maxCol) return 0;
	if(graph[row][col] != '*') return 0;

	graph[row][col] = '#';
	int ret = 1;

	for(int i=0; i<4; i++) ret += dfs(graph, row+r[i], col+c[i], maxRow, maxCol);

	return ret;
}


int main(void) {
	int row, col;
	while(cin >> col >> row) {
		int answer = 0;
		char graph[1100][100];
		for(int i=0; i<row; i++)
			for(int j=0; j<col; j++) cin >> graph[i][j];
		for(int i=0; i<row; i++) {
			for(int j=0; j<col; j++) {
				if(graph[i][j] == '*') {
					answer = maxi(answer, dfs(graph, i, j, row, col));
				}
			}
		}
		cout << answer << endl;
	}
}

#include<iostream>
#include<cstdio>
#include<queue>

#define INTMAX 2147483647

using namespace std;

void play(int num, int goal, int visited[], bool isPrime[]) {
	int x, y;
	char strNum[5];
	queue <int> que;
	que.push(num);
	visited[num] = 0;
	while(!que.empty()) {
		x = que.front();
		que.pop();
		if(x == goal) return;
		for(int i=0; i<4; i++) {
			for(int j=0; j<=9; j++) {
				sprintf(strNum, "%d", x);
				strNum[i] = j + '0';
				sscanf(strNum, "%d", &y);
				if(y < 1000) continue;
				if(isPrime[y] == false) continue;
				if(visited[x]+1 < visited[y]) { visited[y] = visited[x] + 1; que.push(y); }
			}
		}
	}
}

int main(void) {
	bool isPrime[10000];
	isPrime[0] = isPrime[1] = false;
	for(int i=2; i<10000; i++) isPrime[i] = true;
	for(int i=2; i<5000; i++) {
		if(isPrime[i] == false) continue;
		for(int j=2; i*j<10000; j++) isPrime[i*j] = false;
	}

	int tcase, num, goal, answer;
	int visited[10000];

	cin >> tcase;
	while(tcase--) {
		cin >> num >> goal;
		for(int i=0; i<10000; i++) visited[i] = INTMAX;
		play(num, goal, visited, isPrime);
		cout << visited[goal] << endl;
	}
	return 0;
}

#include<iostream>  
#include<cstring>  
#include<vector>  
#include<stdio.h>  
#define NOT 0  
#define MALE 1  
#define FEMALE 2  
using namespace std;  
bool chk;  
vector<vector<int> > list; //간선들  
int visited[2000];  
bool vis[2000];  
void dfs(int s,int state)  

{
	if(visited[s] == NOT)  
		visited[s] = state;  
	else 
		return;  
	for(unsigned int i = 0; i < list[s].size();i++)  
	{  
		if(visited[list[s][i]] == NOT)  
		{  
			if(state == FEMALE)  
				dfs(list[s][i],MALE);           //다음 정점은 수컷으로  
			else if(state == MALE)  
				dfs(list[s][i],FEMALE);  
		}  
	}  
}  

void dfs_process(int s)  

{  
	if(!vis[s]) vis[s] = true;  
	else if(chk) return;  
	else return;  
	for(unsigned int i = 0 ; i < list[s].size();i++)  
	{ 
		if(visited[s] == visited[list[s][i]])  
			chk = true;     //성이 모호해 지는 관계  
		if(!vis[list[s][i]])  
			dfs_process(list[s][i]);  
	}  
}  

int main()  

{  
	int T,n,m;  
	scanf("%d",&T);  
	for(int aa = 0; aa < T;aa++)  
	{  
		scanf("%d %d",&n,&m);  
		list.resize(n,vector<int>(0));  
		memset(visited,NOT,sizeof(visited));  
		memset(vis,false,sizeof(vis));  
		chk = false;  
		for(int i = 0; i < n;i++)  
			list[i].clear();  
		for(int i = 0; i < m ; i++)  
		{  
			int p,q;  
			scanf("%d %d",&p,&q);  
			list[p-1].push_back(q-1); //리스트 구현  
			list[q-1].push_back(p-1);  
		}  
		for(int i = 0; i < n;i++)  
		{  
			if(visited[i] == NOT)  

				dfs(i,FEMALE);  
			dfs_process(i);  
		}  
		printf("Scenario #%d:\n",aa+1);  
		if(chk)     //chk가 참이면 성이 모호해 지는 관계를 뜻함  
			printf("Suspicious bugs found!\n");  
		else 
			printf("No suspicious bugs found!\n");  
		printf("\n");
	}  
	return 0;  

}

#include<iostream>
#include<queue>
#include<utility>
#include<set>
using namespace std;

int maxRow, maxCol;
pair <int, int> start;
pair <int, int> end;
int sr, sc, er, ec;
int r[] = {0, 0, 1, -1};
int c[] = {1, -1, 0, 0};
char graph[110][110];

int bfs() {
	set < pair <int, int> > s;
	queue < pair <int, int> > que;
	queue < int > move;
	pair < int, int > posi;
	pair < int, int > step[4];
	que.push(start);
	move.push(0);
	int visited = 0;
	int moving;
	while(1) {
		if(que.empty()) return -1;
		posi = que.front();
		moving = move.front();
		//cout << posi.first << ' ' << posi.second << ' ' << moving << endl;
		que.pop();
		move.pop();
		if(graph[posi.first][posi.second] == 'E') return moving;
		visited = moving+1;
		for(int i=0; i<4; i++) step[i] = posi;
		step[0].first++;
		step[1].first--;
		step[2].second++;
		step[3].second--;
		for(int i=0; i<4; i++) {
			if(step[i].first < 0 || step[i].first >= maxRow) continue;
			if(step[i].second < 0 || step[i].second >= maxRow) continue;
			if(s.find(step[i]) != s.end()) continue;
			if(graph[step[i].first][step[i].second] == '#') continue;
			que.push(step[i]);
			move.push(visited);
			s.insert(step[i]);
		}
	}
	return -1;
}

int main(void) {
	int tcase;
	cin >> tcase;
	while(tcase--) {
		cin >> maxRow >> maxCol;
		for(int i=0; i<maxRow; i++) {
			for(int j=0; j<maxCol; j++) {
				cin >> graph[i][j];
				if(graph[i][j] == 'S') { start.first = i; start.second = j; }
				if(graph[i][j] == 'E') { end.first = i; end.second = j; }
			}
		}
		cout << bfs() << endl;
	}
}
